import{_ as o,c as t,a2 as i,o as a}from"./chunks/framework.CKGreiRV.js";const h=JSON.parse('{"title":"Migration from VitePress 0.x","description":"","frontmatter":{},"headers":[],"relativePath":"resource/vitepress/guide/migration-from-vitepress-0.md","filePath":"resource/vitepress/guide/migration-from-vitepress-0.md","lastUpdated":1732720247000}'),r={name:"resource/vitepress/guide/migration-from-vitepress-0.md"};function d(n,e,s,c,l,m){return a(),t("div",null,e[0]||(e[0]=[i('<h1 id="migration-from-vitepress-0-x" tabindex="-1">Migration from VitePress 0.x <a class="header-anchor" href="#migration-from-vitepress-0-x" aria-label="Permalink to &quot;Migration from VitePress 0.x&quot;">​</a></h1><details class="details custom-block"><summary>Source text</summary><p><a href="https://github.com/vuejs/vitepress/blob/3c40e9d9a8443433f49599111ee571d569de530d/docs/en/guide/migration-from-vitepress-0.md" target="_blank" rel="noreferrer">https://github.com/vuejs/vitepress/blob/3c40e9d9a8443433f49599111ee571d569de530d/docs/en/guide/migration-from-vitepress-0.md</a></p></details><p>If you&#39;re coming from VitePress 0.x version, there&#39;re several breaking changes due to new features and enhancement. Please follow this guide to see how to migrate your app over to the latest VitePress.</p><h2 id="app-config" tabindex="-1">App Config <a class="header-anchor" href="#app-config" aria-label="Permalink to &quot;App Config&quot;">​</a></h2><ul><li>The internationalization feature is not yet implemented.</li></ul><h2 id="theme-config" tabindex="-1">Theme Config <a class="header-anchor" href="#theme-config" aria-label="Permalink to &quot;Theme Config&quot;">​</a></h2><ul><li><code>sidebar</code> option has changed its structure. <ul><li><code>children</code> key is now named <code>items</code>.</li><li>Top level item may not contain <code>link</code> at the moment. We&#39;re planning to bring it back.</li></ul></li><li><code>repo</code>, <code>repoLabel</code>, <code>docsDir</code>, <code>docsBranch</code>, <code>editLinks</code>, <code>editLinkText</code> are removed in favor of more flexible api. <ul><li>For adding GitHub link with icon to the nav, use <a href="./../reference/default-theme-nav.html#navigation-links">Social Links</a> feature.</li><li>For adding &quot;Edit this page&quot; feature, use <a href="./../reference/default-theme-edit-link.html">Edit Link</a> feature.</li></ul></li><li><code>lastUpdated</code> option is now split into <code>config.lastUpdated</code> and <code>themeConfig.lastUpdatedText</code>.</li><li><code>carbonAds.carbon</code> is changed to <code>carbonAds.code</code>.</li></ul><h2 id="frontmatter-config" tabindex="-1">Frontmatter Config <a class="header-anchor" href="#frontmatter-config" aria-label="Permalink to &quot;Frontmatter Config&quot;">​</a></h2><ul><li><code>home: true</code> option has changed to <code>layout: home</code>. Also, many Homepage related settings have been modified to provide additional features. See <a href="./../reference/default-theme-home-page.html">Home Page guide</a> for details.</li><li><code>footer</code> option is moved to <a href="./../reference/default-theme-config.html#footer"><code>themeConfig.footer</code></a>.</li></ul>',9)]))}const u=o(r,[["render",d]]);export{h as __pageData,u as default};
