import{_ as e,o,c,O as d}from"./chunks/framework.a2eb6a87.js";const g=JSON.parse('{"title":"RAGの文法規則","description":"","frontmatter":{},"headers":[],"relativePath":"wiki/rag.md","filePath":"wiki/rag.md"}'),l={name:"wiki/rag.md"},t=d(`<h1 id="ragの文法規則" tabindex="-1">RAGの文法規則 <a class="header-anchor" href="#ragの文法規則" aria-label="Permalink to &quot;RAGの文法規則&quot;">​</a></h1><p>この文書では、例えば<code>NOVEL_TAG_NAME</code>が<code>x</code>の<code>NOVEL_TAG</code>を<code>x</code>タグと呼ぶ。</p><h2 id="文字コード" tabindex="-1">文字コード <a class="header-anchor" href="#文字コード" aria-label="Permalink to &quot;文字コード&quot;">​</a></h2><ul><li>utf-8</li></ul><h2 id="スクリプトファイルの拡張子" tabindex="-1">スクリプトファイルの拡張子 <a class="header-anchor" href="#スクリプトファイルの拡張子" aria-label="Permalink to &quot;スクリプトファイルの拡張子&quot;">​</a></h2><ul><li><code>.rag</code></li><li><code>.rags</code></li></ul><h2 id="トークン" tabindex="-1">トークン <a class="header-anchor" href="#トークン" aria-label="Permalink to &quot;トークン&quot;">​</a></h2><h3 id="空白" tabindex="-1">空白 <a class="header-anchor" href="#空白" aria-label="Permalink to &quot;空白&quot;">​</a></h3><ul><li><code>LF</code>: <code>0x0A</code></li><li><code>CR</code>: <code>0x0D</code></li><li><code>EOL</code>: <code>&lt;LF&gt;</code>, <code>&lt;CR&gt;</code>, <code>&lt;CR&gt;&lt;LF&gt;</code> のいずれか</li><li><code>TAB_H</code>: <code>0x09</code></li><li><code>TAB_V</code>: <code>0x0B</code></li><li><code>TAB</code>: <code>&lt;TAB_H&gt;</code>, <code>&lt;TAB_V&gt;</code> のいずれか</li><li><code>FF</code>: <code>0x0C</code></li><li><code>SPACE_ASCII</code>: <code>0x20</code></li><li><code>SPACE_UTF8_2BYTE</code>: <code>0xC2A0</code></li><li><code>SPACE_UTF8_3BYTE</code>: <code>0xE38080</code></li><li><code>SPACE</code>: <code>&lt;SPACE_ASCII&gt;</code>, <code>&lt;SPACE_UTF8_2BYTE&gt;</code>, <code>&lt;SPACE_UTF8_3BYTE&gt;</code> のいずれか</li><li><code>BLANK</code>: <code>&lt;SPACE&gt;</code>, <code>&lt;EOL&gt;</code>, <code>&lt;TAB&gt;</code>, <code>&lt;FF&gt;</code> のいずれか</li></ul><h3 id="記号" tabindex="-1">記号 <a class="header-anchor" href="#記号" aria-label="Permalink to &quot;記号&quot;">​</a></h3><ul><li><code>DOT</code>: <code>.</code></li><li><code>COMMA</code>: <code>,</code></li><li><code>EQUAL</code>: <code>=</code></li><li><code>SQUARE_L</code>: <code>[</code></li><li><code>SQUARE_R</code>: <code>]</code></li><li><code>CURLY_L</code>: <code>{</code></li><li><code>CURLY_R</code>: <code>}</code></li><li><code>ANGLE_L</code>: <code>&lt;</code></li><li><code>ANGLE_R</code>: <code>&gt;</code></li><li><code>ROUND_L</code>: <code>(</code></li><li><code>ROUND_R</code>: <code>)</code></li><li><code>PLUS</code>: <code>+</code></li><li><code>HYPHEN</code>: <code>-</code></li><li><code>ASTERISK</code>: <code>*</code></li><li><code>SLASH</code>: <code>/</code></li><li><code>BACK_SLASH</code>: <code>0x5C</code></li><li><code>SEMICOLON</code>: <code>;</code></li><li><code>COLON</code>: <code>:</code></li><li><code>DOLLAR</code>: <code>$</code></li><li><code>PERCENT</code>: <code>%</code></li><li><code>SHARP</code>: <code>#</code></li><li><code>QUOTE_SINGLE</code>: <code>&#39;</code></li><li><code>QUOTE_DOUBLE</code>: <code>&quot;</code></li><li><code>QUOTE_BACK</code>: <code>0x60</code></li><li><code>UNDERSCORE</code>: <code>_</code></li><li><code>QUESTION</code>: <code>?</code></li><li><code>EXCLAMATION</code>: <code>!</code></li><li><code>AND</code>: <code>&amp;</code></li><li><code>AT</code>: <code>@</code></li><li><code>HAT</code>: <code>^</code></li><li><code>TILDE</code>: <code>~</code></li><li><code>OR</code>: <code>|</code></li></ul><h3 id="エスケープ文字" tabindex="-1">エスケープ文字 <a class="header-anchor" href="#エスケープ文字" aria-label="Permalink to &quot;エスケープ文字&quot;">​</a></h3><ul><li><code>ESC_DOT</code>: <code>&lt;BACK_SLASH&gt;&lt;DOT&gt;</code></li><li><code>ESC_COMMA</code>: <code>&lt;BACK_SLASH&gt;&lt;COMMA&gt;</code></li><li><code>ESC_EQUAL</code>: <code>&lt;BACK_SLASH&gt;&lt;EQUAL&gt;</code></li><li><code>ESC_SQUARE_L</code>: <code>&lt;BACK_SLASH&gt;&lt;SQUARE_L&gt;</code></li><li><code>ESC_SQUARE_R</code>: <code>&lt;BACK_SLASH&gt;&lt;SQUARE_R&gt;</code></li><li><code>ESC_CURLY_L</code>: <code>&lt;BACK_SLASH&gt;&lt;CURLY_L&gt;</code></li><li><code>ESC_CURLY_R</code>: <code>&lt;BACK_SLASH&gt;&lt;CURLY_R&gt;</code></li><li><code>ESC_ANGLE_L</code>: <code>&lt;BACK_SLASH&gt;&lt;ANGLE_L&gt;</code></li><li><code>ESC_ANGLE_R</code>: <code>&lt;BACK_SLASH&gt;&lt;ANGLE_R&gt;</code></li><li><code>ESC_ROUND_L</code>: <code>&lt;BACK_SLASH&gt;&lt;ROUND_L&gt;</code></li><li><code>ESC_ROUND_R</code>: <code>&lt;BACK_SLASH&gt;&lt;ROUND_R&gt;</code></li><li><code>ESC_PLUS</code>: <code>&lt;BACK_SLASH&gt;&lt;PLUS&gt;</code></li><li><code>ESC_HYPHEN</code>: <code>&lt;BACK_SLASH&gt;&lt;HYPHEN&gt;</code></li><li><code>ESC_ASTERISK</code>: <code>&lt;BACK_SLASH&gt;&lt;ASTERISK&gt;</code></li><li><code>ESC_SLASH</code>: <code>&lt;BACK_SLASH&gt;&lt;SLASH&gt;</code></li><li><code>ESC_BACK_SLASH</code>: <code>&lt;BACK_SLASH&gt;&lt;BACK_SLASH&gt;</code></li><li><code>ESC_SEMICOLON</code>: <code>&lt;BACK_SLASH&gt;&lt;SEMICOLON&gt;</code></li><li><code>ESC_COLON</code>: <code>&lt;BACK_SLASH&gt;&lt;COLON&gt;</code></li><li><code>ESC_DOLLAR</code>: <code>&lt;BACK_SLASH&gt;&lt;DOLLAR&gt;</code></li><li><code>ESC_PERCENT</code>: <code>&lt;BACK_SLASH&gt;&lt;PERCENT&gt;</code></li><li><code>ESC_SHARP</code>: <code>&lt;BACK_SLASH&gt;&lt;SHARP&gt;</code></li><li><code>ESC_QUOTE_SINGLE</code>: <code>&lt;BACK_SLASH&gt;&lt;QUOTE_SINGLE&gt;</code></li><li><code>ESC_QUOTE_DOUBLE</code>: <code>&lt;BACK_SLASH&gt;&lt;QUOTE_DOUBLE&gt;</code></li><li><code>ESC_QUOTE_BACK</code>: <code>&lt;BACK_SLASH&gt;&lt;QUOTE_BACK&gt;</code></li><li><code>ESC_UNDERSCORE</code>: <code>&lt;BACK_SLASH&gt;&lt;UNDERSCORE&gt;</code></li><li><code>ESC_QUESTION</code>: <code>&lt;BACK_SLASH&gt;&lt;QUESTION&gt;</code></li><li><code>ESC_EXCLAMATION</code>: <code>&lt;BACK_SLASH&gt;&lt;EXCLAMATION&gt;</code></li><li><code>ESC_AND</code>: <code>&lt;BACK_SLASH&gt;&lt;AND&gt;</code></li><li><code>ESC_AT</code>: <code>&lt;BACK_SLASH&gt;&lt;AT&gt;</code></li><li><code>ESC_HAT</code>: <code>&lt;BACK_SLASH&gt;&lt;HAT&gt;</code></li><li><code>ESC_TILDE</code>: <code>&lt;BACK_SLASH&gt;&lt;TILDE&gt;</code></li><li><code>ESC_OR</code>: <code>&lt;BACK_SLASH&gt;&lt;OR&gt;</code></li><li><code>ESC_TAB</code>: <code>&lt;BACK_SLASH&gt;t</code></li><li><code>ESC_ENTER</code>: <code>&lt;BACK_SLASH&gt;n</code></li><li><code>ESC_NULL</code>: <code>&lt;BACK_SLASH&gt;0</code></li><li><code>ESC_CHAR</code>: <code>&lt;ESC_DOT&gt;</code>, <code>&lt;ESC_COMMA&gt;</code>, <code>&lt;ESC_EQUAL&gt;</code>, <code>&lt;ESC_SQUARE_L&gt;</code>, <code>&lt;ESC_SQUARE_R&gt;</code>, <code>&lt;ESC_CURLY_L&gt;</code>, <code>&lt;ESC_CURLY_R&gt;</code>, <code>&lt;ESC_ANGLE_L&gt;</code>, <code>&lt;ESC_ANGLE_R&gt;</code>, <code>&lt;ESC_ROUND_L&gt;</code>, <code>&lt;ESC_ROUND_R&gt;</code>, <code>&lt;ESC_PLUS&gt;</code>, <code>&lt;ESC_HYPHEN&gt;</code>, <code>&lt;ESC_ASTERISK&gt;</code>, <code>&lt;ESC_SLASH&gt;</code>, <code>&lt;ESC_BACK_SLASH&gt;</code>, <code>&lt;ESC_SEMICOLON&gt;</code>, <code>&lt;ESC_COLON&gt;</code>, <code>&lt;ESC_DOLLAR&gt;</code>, <code>&lt;ESC_PERCENT&gt;</code>, <code>&lt;ESC_SHARP&gt;</code>, <code>&lt;ESC_QUOTE_SINGLE&gt;</code>, <code>&lt;ESC_QUOTE_DOUBLE&gt;</code>, <code>&lt;ESC_QUOTE_BACK&gt;</code>, <code>&lt;ESC_UNDERSCORE&gt;</code>, <code>&lt;ESC_QUESTION&gt;</code>, <code>&lt;ESC_EXCLAMATION&gt;</code>, <code>&lt;ESC_AND&gt;</code>, <code>&lt;ESC_AT&gt;</code>, <code>&lt;ESC_HAT&gt;</code>, <code>&lt;ESC_TILDE&gt;</code>, <code>&lt;ESC_OR&gt;</code>, <code>&lt;ESC_TAB&gt;</code>, <code>&lt;ESC_ENTER&gt;</code>, <code>&lt;ESC_NULL&gt;</code> のいずれか</li></ul><h3 id="シンボル" tabindex="-1">シンボル <a class="header-anchor" href="#シンボル" aria-label="Permalink to &quot;シンボル&quot;">​</a></h3><ul><li><code>QUOTE</code>: <code>&lt;QUOTE_SINGLE&gt;</code>, <code>&lt;QUOTE_DOUBLE&gt;</code>, <code>&lt;QUOTE_BACK&gt;</code>のいずれか</li><li><code>SYMBOL_NON_5C_QUOTE</code>: <code>&lt;DOT&gt;</code>, <code>&lt;COMMA&gt;</code>, <code>&lt;EQUAL&gt;</code>, <code>&lt;SQUARE_L&gt;</code>, <code>&lt;SQUARE_R&gt;</code>, <code>&lt;CURLY_L&gt;</code>, <code>&lt;CURLY_R&gt;</code>, <code>&lt;ANGLE_L&gt;</code>, <code>&lt;ANGLE_R&gt;</code>, <code>&lt;ROUND_L&gt;</code>, <code>&lt;ROUND_R&gt;</code>, <code>&lt;PLUS&gt;</code>, <code>&lt;HYPHEN&gt;</code>, <code>&lt;ASTERISK&gt;</code>, <code>&lt;SLASH&gt;</code>, <code>&lt;SEMICOLON&gt;</code>, <code>&lt;COLON&gt;</code>, <code>&lt;DOLLAR&gt;</code>, <code>&lt;PERCENT&gt;</code>, <code>&lt;SHARP&gt;</code>, <code>&lt;UNDERSCORE&gt;</code>, <code>&lt;QUESTION&gt;</code>, <code>&lt;EXCLAMATION&gt;</code>, <code>&lt;AND&gt;</code>, <code>&lt;AT&gt;</code>, <code>&lt;HAT&gt;</code>, <code>&lt;TILDE&gt;</code>, <code>&lt;OR&gt;</code> のいずれか</li><li><code>SYMBOL_NON_5C</code>: <code>&lt;SYMBOL_NON_0x5C_QUOTE&gt;</code>, <code>&lt;QUOTE&gt;</code> のいずれか</li><li><code>SYMBOL</code>: <code>&lt;SYMBOL_NON_5C&gt;</code>, <code>&lt;BACK_SLASH&gt;</code> のいずれか</li></ul><h3 id="アルファベット" tabindex="-1">アルファベット <a class="header-anchor" href="#アルファベット" aria-label="Permalink to &quot;アルファベット&quot;">​</a></h3><ul><li><code>ALPHABET</code>: <code>a</code>, <code>b</code>, <code>c</code>, <code>d</code>, <code>e</code>, <code>f</code>, <code>g</code>, <code>h</code>, <code>i</code>, <code>j</code>, <code>k</code>, <code>l</code>, <code>m</code>, <code>n</code>, <code>o</code>, <code>p</code>, <code>q</code>, <code>r</code>, <code>s</code>, <code>t</code>, <code>u</code>, <code>v</code>, <code>w</code>, <code>x</code>, <code>y</code>, <code>z</code>, <code>A</code>, <code>B</code>, <code>C</code>, <code>D</code>, <code>E</code>, <code>F</code>, <code>G</code>, <code>H</code>, <code>I</code>, <code>J</code>, <code>K</code>, <code>L</code>, <code>M</code>, <code>N</code>, <code>O</code>, <code>P</code>, <code>Q</code>, <code>R</code>, <code>S</code>, <code>T</code>, <code>U</code>, <code>V</code>, <code>W</code>, <code>X</code>, <code>Y</code>, <code>Z</code> のいずれか</li></ul><h3 id="utf-8" tabindex="-1">UTF-8 <a class="header-anchor" href="#utf-8" aria-label="Permalink to &quot;UTF-8&quot;">​</a></h3><ul><li><code>U1</code>: <code>0x80</code>から<code>0xBF</code>のいずれか</li><li><code>U2</code>: <code>0xC2</code>から<code>0xDF</code>のいずれか</li><li><code>U3</code>: <code>0xE0</code>から<code>0xEF</code>のいずれか</li><li><code>U4</code>: <code>0xF0</code>から<code>0xF4</code>のいずれか</li><li><code>UTF8</code>: <code>&lt;U2&gt;&lt;U&gt;</code>、<code>&lt;U3&gt;&lt;U1&gt;&lt;U1&gt;</code>、<code>&lt;U4&gt;&lt;U1&gt;&lt;U1&gt;&lt;U1&gt;</code>のいずれか</li></ul><h3 id="予約語" tabindex="-1">予約語 <a class="header-anchor" href="#予約語" aria-label="Permalink to &quot;予約語&quot;">​</a></h3><ul><li><code>NONE</code>: <code>none</code></li><li><code>NULL</code>: <code>null</code></li><li><code>BOOLEAN</code>: <code>true</code>、<code>false</code> のいずれか</li></ul><h3 id="数値" tabindex="-1">数値 <a class="header-anchor" href="#数値" aria-label="Permalink to &quot;数値&quot;">​</a></h3><ul><li><code>DIGIT_NON_ZERO</code>: <code>1</code>, <code>2</code>, <code>3</code>, <code>4</code>, <code>5</code>, <code>6</code>, <code>7</code>, <code>8</code>, <code>9</code> のいずれか</li><li><code>DIGIT</code>: <code>0</code>, <code>&lt;DIGIT_NON_ZERO&gt;</code> のいずれか</li><li><code>DIGITS</code>: <code>&lt;DIGIT&gt;</code>の1つ以上の連続</li><li><code>INTEGER</code>: <code>&lt;DIGIT&gt;</code>, <code>&lt;DIGIT_NON_ZERO&gt;&lt;DIGITS&gt;</code></li><li><code>FLOAT</code>: <code>&lt;DIGIT&gt;&lt;DOT&gt;&lt;DIGITS&gt;</code></li><li><code>DIGIT_HEX</code>: <code>&lt;DIGIT&gt;</code>, <code>a</code>, <code>b</code>, <code>c</code>, <code>d</code>, <code>e</code>, <code>f</code>, <code>A</code>, <code>B</code>, <code>C</code>, <code>D</code>, <code>E</code>, <code>F</code> のいずれか</li><li><code>DIGITS_HEX</code>: <code>&lt;DIGIT_HEX&gt;</code>の1つ以上の連続</li><li><code>NUMERAL_HEX</code>: <code>0x&lt;DIGITS_HEX&gt;</code>, <code>0X&lt;DIGITS_HEX&gt;</code></li><li><code>SIGN</code>: <code>&lt;PLUS&gt;</code>, <code>&lt;HYPHEN&gt;</code> のいずれか</li><li><code>SIGNED_INTEGER</code>: <code>&lt;SIGN&gt;&lt;INTEGER&gt;</code></li><li><code>SIGNED_FLOAT</code>: <code>&lt;SIGN&gt;&lt;FLOAT&gt;</code></li></ul><h3 id="色" tabindex="-1">色 <a class="header-anchor" href="#色" aria-label="Permalink to &quot;色&quot;">​</a></h3><ul><li><code>HEX_COLOR_RRGGBB</code>: <code>&lt;SHARP&gt;&lt;DIGIT_HEX&gt;&lt;DIGIT_HEX&gt;&lt;DIGIT_HEX&gt;&lt;DIGIT_HEX&gt;&lt;DIGIT_HEX&gt;&lt;DIGIT_HEX&gt;</code></li></ul><h2 id="文法" tabindex="-1">文法 <a class="header-anchor" href="#文法" aria-label="Permalink to &quot;文法&quot;">​</a></h2><ul><li><p><code>RAG_SCRIPT</code>: <code>&lt;BLANK_BLOCK&gt;</code>, <code>&lt;SCRIPT_BLOCK&gt;</code>, <code>&lt;NOVEL_BLOCK&gt;</code>, で構成される</p></li><li><p><code>BLANK_BLOCK</code>: <code>&lt;BLANK_LINE&gt;</code> の連続が1つ以上</p></li><li><p><code>SCRIPT_BLOCK</code>: <code>&lt;SCRIPT_LINE&gt;</code> の連続が1つ以上</p></li><li><p><code>NOVEL_BLOCK</code>: <code>&lt;NOVEL_LINE&gt;</code> の連続が1つ以上</p></li><li><p><code>BLANK_LINE</code>: <code>&lt;BLANK&gt;</code> で構成される行</p></li><li><p><code>SCRIPT_LINE</code>: <code>&lt;SPACE_ASCII&gt;</code>の連続が行頭から4つ以上あり、その後<code>&lt;SCRIPT_KEYS&gt;</code>で構成される行</p></li><li><p><code>SCRIPT_KEYS</code>: <code>&lt;ALPHABET&gt;</code>, <code>&lt;DIGIT&gt;</code>, <code>&lt;UNDERSCORE&gt;</code>, <code>&lt;UTF8&gt;</code> で構成される</p></li><li><p><code>NOVEL_LINE</code>: <code>&lt;SPACE_ASCII&gt;</code>の連続が存在しないか行頭から3つ以下あり、その後<code>&lt;NOVEL_ELEMENTS&gt;</code>で構成される行</p></li><li><p><code>NOVEL_ELEMENTS</code>: <code>&lt;NOVEL_ELEMENT&gt;</code>の連続が1つ以上</p></li><li><p><code>NOVEL_ELEMENT</code>: <code>&lt;NOVEL_TAG&gt;</code>、<code>&lt;NOVEL_STRING&gt;</code>、<code>&lt;NOVEL_LABEL&gt;</code>、<code>&lt;NOVEL_SCRIPT_BLOCK&gt;</code>、<code>&lt;NOVEL_MACRO_BLOCK&gt;</code>、<code>&lt;NOVEL_LINK_BLOCK&gt;</code>、<code>&lt;NOVEL_SUBROUNTINE&gt;</code>、<code>&lt;NOVEL_IF_BLOCK&gt;</code>、<code>&lt;NOVEL_IGNORE_BLOCK&gt;</code>、 <code>&lt;NOVEL_COMMENT_KAG&gt;</code>、<code>&lt;NOVEL_MESSAGE&gt;</code> のいずれか</p></li><li><p><code>NOVEL_MESSAGE</code>: <code>&lt;NOVEL_MESSAGE_KEYS&gt;</code>で構成される。<code>&lt;NOVEL_TAG&gt;</code>、<code>&lt;NOVEL_LABEL&gt;</code>、<code>&lt;NOVEL_COMMENT&gt;</code>、<code>&lt;NOVEL_SCRIPT_BLOCK&gt;</code> に該当しない</p></li><li><p><code>NOVEL_COMMENT</code>: <code>&lt;NOVEL_HAT_COMMENT&gt;</code>, <code>&lt;NOVEL_SEMICOLON_COMMENT&gt;</code> のいずれか</p></li><li><p><code>NOVEL_HAT_COMMENT</code>: <code>^</code>から行末まで。<code>&lt;NOVEL_SCRIPT_BLOCK&gt;</code>と<code>&lt;NOVEL_STRING&gt;</code>内を除く</p></li><li><p><code>NOVEL_SEMICOLON_COMMENT</code>: 行頭の<code>;</code>から行末まで。<code>&lt;NOVEL_SCRIPT_BLOCK&gt;</code>と<code>&lt;NOVEL_STRING&gt;</code>内を除く</p></li><li><p><code>NOVEL_IGNORE_BLOCK</code>: <code>ignore</code>タグから<code>endignore</code>タグまで。間にはラベルを挟まないように。</p></li><li><p><code>NOVEL_IF_BLOCK</code>: <code>if</code>タグから<code>endif</code>タグまで。間にはラベルを挟んではいけない。</p></li><li><p><code>NOVEL_SUBROUTINE</code>: <code>&lt;NOVEL_LABEL&gt;</code>から<code>&lt;NOVEL_SUBROUTINE_END&gt;</code>まで</p></li><li><p><code>NOVEL_SUBROUTINE_END</code>: <code>&lt;return&gt;</code>タグあるいは<code>s</code>タグ、またはファイル末尾</p></li><li><p><code>NOVEL_LINK_BLOCK</code>: <code>link</code>タグから<code>endlink</code>タグまで</p></li><li><p><code>NOVEL_MACRO_ATTRIBUTE_ALL</code>: <code>&lt;NOVEL_MACRO_BLOCK&gt;</code>内の<code>&lt;NOVEL_TAG&gt;</code>の<code>&lt;NOVEL_TAG_NAME&gt;</code>の次の<code>*</code></p></li><li><p><code>NOVEL_MACRO_ATTRIBUTE_DEFAULT</code>: <code>&lt;NOVEL_MACRO_ATTRIBUTE_VALUE&gt;</code>の次が<code>&lt;OR&gt;</code>であった場合の次の<code>&lt;NOVEL_KEYS&gt;</code></p></li><li><p><code>NOVEL_MACRO_ATTRIBUTE_VALUE</code>: <code>&lt;NOVEL_MACRO_BLOCK&gt;</code>でのみ使用できる<code>&lt;NOVEL_TAG_ATTRIBUTE&gt;</code>。必ず<code>%</code>で始まる</p></li><li><p><code>NOVEL_MACRO_BLOCK</code>: <code>macro</code>タグから<code>endmacro</code>タグまで</p></li><li><p><code>NOVEL_SCRIPT_BLOCK</code>: <code>iscript</code>タグから<code>endscript</code>まで</p></li><li><p><code>NOVEL_SAVE_NAME</code>: <code>&lt;NOVEL_LABEL_NAME&gt;</code>の次の<code>&lt;NOVEL_KEYS&gt;</code>または<code>&lt;NOVEL_ENTITY&gt;</code>。ただし<code>&lt;NOVEL_LABEL_NAME&gt;</code>と<code>&lt;KAB_SAVE_NAME&gt;</code>の間には<code>&lt;OR&gt;</code>が必要</p></li><li><p><code>NOVEL_LABEL_NAME</code>: <code>&lt;NOVEL_LABEL&gt;</code>内の最初の<code>&lt;NOVEL_KEYS&gt;</code>。<code>&lt;NOVEL_KEYS&gt;</code>よりも<code>&lt;OR&gt;</code>が先にあった場合は<code>&lt;NOVEL_LABEL_NAME&gt;</code>が省略されたとみなされ、<code>&lt;NOVEL_LABEL_NAME&gt;</code>は直前の<code>&lt;NOVEL_LABEL_NAME&gt;</code> + <code>:</code> + <code>&lt;DIGIT&gt;</code>となる。この<code>&lt;DIGIT&gt;</code>は2から始まり、省略が続く場合、値が1増加した数値になる。</p></li><li><p><code>NOVEL_LABEL</code>: <code>&lt;NOVEL_LABEL_TOP_FILE&gt;</code>あるいは<code>&lt;NOVEL_LABEL_NOMAL&gt;</code></p></li><li><p><code>NOVEL_LABEL_NORMAL</code>: 行頭が <code>&lt;ASTERISK&gt;</code> で始まる行</p></li><li><p><code>NOVEL_LABEL_TOP_FILE</code>: ファイルの先頭位置</p></li><li><p><code>NOVEL_STRING</code>: <code>&lt;NOVEL_STRING_SINGLE&gt;</code>、<code>&lt;NOVEL_STRING_DOUBLE&gt;</code> のいずれか</p></li><li><p><code>NOVEL_STRING_DOUBLE</code>: <code>&lt;QUOTE_DOUBLE&gt;</code> で囲まれた範囲。内包する<code>&lt;QUOTE_SINGLE&gt;</code>は文字列とみなされる。ネストは許可されない。</p></li><li><p><code>NOVEL_STRING_SINGLE</code>: <code>&lt;QUOTE_SINGLE&gt;</code> で囲まれた範囲。内包する<code>&lt;QUOTE_DOUBLE&gt;</code>は文字列とみなされる。ネストは許可されない。</p></li><li><p><code>NOVEL_TAG_ATTRIBUTE_VALUE</code>: <code>&lt;NOVEL_TAG_ATTRIBUTE&gt;</code>の等式の右辺</p></li><li><p><code>NOVEL_TAG_ATTRIBUTE_NAME</code>: <code>&lt;NOVEL_TAG_ATTRIBUTE&gt;</code>の等式の左辺</p></li><li><p><code>NOVEL_TAG_ATTRIBUTE</code>: <code>&lt;NOVEL_TAG_NAME&gt;</code>の後から末尾までの<code>&lt;NOVEL_KEYS&gt;&lt;EQUAL&gt;&lt;NOVEL_KEYS&gt;</code>の等式。等式中の空白文字は無視される</p></li><li><p><code>NOVEL_TAG_NAME</code>: <code>&lt;NOVEL_TAG&gt;</code>内の最初の<code>&lt;NOVEL_KEYS&gt;</code></p></li><li><p><code>NOVEL_TAG</code>: <code>&lt;NOVEL_TAG_SQUARE_BRACKET&gt;</code>または<code>&lt;NOVEL_TAG_AT&gt;</code></p></li><li><p><code>NOVEL_TAG_AT</code>: 行頭の<code>&lt;AT&gt;</code>から行末まで。行頭の空白文字は無視すること</p></li><li><p><code>NOVEL_TAG_SQUARE_BRACKET</code>: <code>&lt;SQUARE_L&gt;</code>から<code>&lt;SQUARE_R&gt;</code>まで。ただし<code>[[</code>で始まる場合は除く。ネストは許可されない</p></li><li><p><code>NOVEL_ENTITY</code>: <code>&lt;AND&gt;f&lt;DOT&gt;&lt;NOVEL_KEYS&gt;</code>, <code>&lt;AND&gt;sf&lt;DOT&gt;&lt;NOVEL_KEYS&gt;</code>, <code>&lt;AND&gt;tf&lt;DOT&gt;&lt;NOVEL_KEYS&gt;</code>, <code>&lt;AND&gt;kag&lt;DOT&gt;&lt;NOVEL_KEYS&gt;</code>のいずれか</p></li><li><p><code>NOVEL_MESSAGE_KEYS</code>: <code>&lt;ALPHABET&gt;</code>, <code>&lt;DIGIT&gt;</code>, <code>&lt;UTF8&gt;</code>, <code>&lt;ESC_CHAR&gt;</code> で構成される</p></li><li><p><code>NOVEL_KEYS</code>: <code>&lt;ALPHABET&gt;</code>, <code>&lt;DIGIT&gt;</code>, <code>&lt;UNDERSCORE&gt;</code>, <code>&lt;UTF8&gt;</code> で構成される</p></li></ul><h2 id="吉里吉里のkagと違うところ" tabindex="-1">吉里吉里のKAGと違うところ <a class="header-anchor" href="#吉里吉里のkagと違うところ" aria-label="Permalink to &quot;吉里吉里のKAGと違うところ&quot;">​</a></h2><ul><li>自動改行、手動改行の切り替え</li></ul><h2 id="ティラノスクリプト拡張記法" tabindex="-1">ティラノスクリプト拡張記法 <a class="header-anchor" href="#ティラノスクリプト拡張記法" aria-label="Permalink to &quot;ティラノスクリプト拡張記法&quot;">​</a></h2><ul><li><code>#</code>+名前でキャラクター名を指定できる</li><li><code>#</code>+名前+<code>:</code>+表情など で表情を指定できる</li></ul><h2 id="拡張記法" tabindex="-1">拡張記法 <a class="header-anchor" href="#拡張記法" aria-label="Permalink to &quot;拡張記法&quot;">​</a></h2><ul><li><code>MESSAGE_BLOCK</code>: <code>message</code>タグから<code>endmessage</code>タグまで</li><li><code>@script</code>タグ。</li><li>スクリプトの途中の行末に<code>\\</code>を挿入ことで、スクリプトを複数行に渡って記述できる</li><li>終了タグに<code>end...</code>の代わりに<code>[/if]</code>を指定できる</li><li>インデントでタグ記号を省略してスクリプトで記述できる</li><li><code>if {} macro {} ignore {}</code>が使える。</li><li><code>for</code> <code>while</code> などが使える</li><li><code>iscript</code>、<code>endscript</code>は無視される。</li><li>スクリプト中はスクリプトに準拠するが基本的に、<code>//</code>,<code>/**/</code>, <code>#</code>コメントを使える</li><li><code>\\{\\{script\\}\\}</code>でスクリプトを呼び出せる。<code>@eval</code>と同等。</li><li><code>[?mruby ?]</code></li><li>キャラ名：行頭+KEYS+<code>:</code> + <code>\\n | (...)</code> でキャラ名の指定ができる。<code>:</code>の後にKEYSが来てはいけない。</li><li><code>キャラ名.表情:</code> で表情を指定できる</li><li>表情指定：キャラ名指定ののち<code>(...)</code>の間に表情指定などができる</li><li>ボイス指定：表情指定ののちに<code>{...}</code>でボイス形式でアニメーションなどを指定できる</li><li>キャラ名と同等の記法でメッセージとして<code>:</code>を使用したい時は<code>\\:</code>を用いる。</li><li>メッセージ行</li><li>メッセージブロック: メッセージ行の連続、空白行またはタグ、インデントブロック、あるいは末尾まで</li><li>メッセージ内に<code>$(x)</code>で表情差分、<code>\${x}</code>でボイス、<code>$&lt;x&gt;</code>で特殊演出、<code>\\{\\{script\\}\\}</code>でスクリプト</li><li>メッセージ内に指定がなければ、事前予約された表情、</li><li><code>-</code> リストでリンクの分岐が作れる（？）</li><li><code>[1]</code> タグ名の部分が数値だったばあいは予約参照</li></ul><h2 id="メモ" tabindex="-1">メモ <a class="header-anchor" href="#メモ" aria-label="Permalink to &quot;メモ&quot;">​</a></h2><ul><li><p>[eval exp=&quot;f.flag1 = 1000&quot;]でスクリプトの式を計算できる</p><ul><li>ゲーム変数であれば f. を、システム変数であれば sf. を、一時変数であれば tf. を付けます</li><li>kag. はKAGシステムへのアクセスに使われる</li><li>delete var で変数削除</li><li>[emb exp=&quot;f.var&quot;]で変数の内容を表示</li><li>[font size=&quot;&amp;f.fontsize&quot;] のように&amp; の後に書かれた属性の値の内容を、TJS2 式として実行して、その結果で属性の値を置き換える</li></ul></li><li><p>cond 属性には TJS式 を指定し、この式を評価した結果が真の時のみにそのタグが実行されます</p><ul><li><code>macro endmacro if else elsif endif ignore endignore iscript endscript</code> のタグをのぞく</li></ul></li><li><p>音声と表情の同期。</p></li></ul><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;">先生: ()</span></span>
<span class="line"><span style="color:#A6ACCD;">わたしもやき{1}が回ったってことかしら？</span></span>
<span class="line"><span style="color:#A6ACCD;">そうでもなければだ{2}ものね。</span></span>
<span class="line"><span style="color:#A6ACCD;">でも、そう{3}かんがえると</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">それでは・・・そういうことかもしれない。</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">私: (face normal) {voice 0%:&quot;78&quot;, 50%:happy, 9%:shy}</span></span>
<span class="line"><span style="color:#A6ACCD;">そういうことであれば、{{script}}</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">少女: (face) {voice}</span></span>
<span class="line"><span style="color:#A6ACCD;">$(1)なるほど、わかりました。</span></span>
<span class="line"><span style="color:#A6ACCD;">$(2)そういうことであれば、気をつけます。</span></span>
<span class="line"><span style="color:#A6ACCD;">$(3)あなたもがんばってください。</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">少女: {</span></span>
<span class="line"><span style="color:#A6ACCD;">    character: {</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">    },</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">}</span></span></code></pre></div><h2 id="toml" tabindex="-1">TOML <a class="header-anchor" href="#toml" aria-label="Permalink to &quot;TOML&quot;">​</a></h2><h3 id="tomlのトークン" tabindex="-1">TOMLのトークン <a class="header-anchor" href="#tomlのトークン" aria-label="Permalink to &quot;TOMLのトークン&quot;">​</a></h3><ul><li><code>TOML_COMMENT</code>: <code>#</code> から行末まで</li><li><code>TOML_KEY</code>: <code>&quot;</code>で囲まれた任意の文字列、またはアルファベット、数字、アンダースコア、ハイフンの連続</li><li><code>TOML_NUMBER</code>: 浮動小数点数または整数。数字とアンダースコアの連続で、アンダースコアは数字の間にだけ現れます。</li><li><code>TOML_BOOLEAN</code>: trueまたはfalse</li><li><code>TOML_DATE</code>: 日付</li><li><code>TOML_DOT</code>: <code>.</code></li><li><code>TOML_COMMA</code>: <code>,</code></li><li><code>TOML_EQ</code>: <code>=</code></li><li><code>TOML_LBRACKET</code>: <code>[</code></li><li><code>TOML_RBRACKET</code>: <code>]</code></li><li><code>TOML_LBRACE</code>: <code>(</code></li><li><code>TOML_RBRACE</code>: <code>)</code></li></ul><h3 id="tomlの文法" tabindex="-1">TOMLの文法 <a class="header-anchor" href="#tomlの文法" aria-label="Permalink to &quot;TOMLの文法&quot;">​</a></h3><ul><li><code>TOML_VALUE</code>: <code>TOML_KEY</code>、<code>TOML_NUMBER</code>、<code>TOML_BOOLEAN</code>、<code>TOML_DATE</code>、<code>TOML_ARRAY</code>、<code>TOML_INLINE_TABLE</code> のいずれかです。</li><li><code>TOML_ARRAY</code>: <code>[]</code>で囲まれた<code>TOML_VALUE</code>のリストです。リスト内の値は<code>,</code>で区切られます。</li><li><code>TOML_INLINE_TABLE</code>: <code>{}</code>で囲まれた<code>TOML_KEY_VALUE</code>のリストです。リスト内の<code>TOML_KEY_VALUE</code>は<code>,</code>で区切られます。ただし、<code>TOML_INLINE_TABLE</code>は単一行に収めるべきです。</li><li><code>TOML_KEY_VALUE</code>: <code>TOML_KEY_OR_STR</code>、<code>=</code>、<code>TOML_VALUEで</code>で構成されます。</li><li><code>TOML_TABLE_ENTRIES</code>: 0つ以上の<code>TOML_KEY_VALUE</code>からなるリストです。</li><li><code>TOML_PATH</code>: <code>TOML_KEY_OR_STR</code>、<code>.</code>、<code>TOML_KEY_OR_STR</code>が0回以上続く形式です。</li><li><code>TOML_TABLE_HEADER</code>: <code>[]</code>で囲まれた<code>TOML_PATH</code>です。</li><li><code>TOML_TABLE_ARRAY_HEADER</code>: <code>[[]]</code>で囲まれた<code>TOML_PATH</code>です。</li><li><code>TOML_TABLE_ARRAY</code>: <code>TOML_TABLE_ARRAY_HEADER</code>と<code>TOML_TABLE_ENTRIES</code>で構成されます。</li><li><code>TOML_TABLE</code>: <code>TOML_TABLE_HEADER</code>と<code>TOMLE_TABLE_ENTRIES</code>で構成されます。</li><li><code>TOML_EXPRESSION</code>: <code>TOML_KEY_VALUE</code>、<code>TOML_TABLE</code>、<code>TOML_TABLE_ARRAY</code>のいずれかです。</li><li><code>TOML_CONTENT</code>: 0個つ以上の<code>TOML_EXPRESSION</code>で構成されます</li></ul>`,41),i=[t];function E(_,L,A,a,O,S){return o(),c("div",null,i)}const C=e(l,[["render",E]]);export{g as __pageData,C as default};
